public class Pro { private int CCPUB = -1; private int CCIO = -1; private int runT = 0; private int waitT = 0; private int arrivalT; private int cpuT; private int total = 0; private String state = "0"; private int cpuBT; private int ioBT; private int NN; private int ioTime = 0; private int CCPUBC = -1; private int CCIOC = -1;public Pro(int NUM, int A, int B, int C, int IO) { this.arrivalT = A; this.cpuT = C; this.cpuBT = B; this.ioBT = IO; this.NN = NUM; }public void sets(String newState) { this.state = newState; }public String gets() { return this.state; }public int getAT() { return this.arrivalT; }public int getNN() { return this.NN; }public int getcpuBT() { return this.cpuBT; }public int getioBT() { return this.ioBT; }public int getTimeNeeded() { return this.cpuT; }public void passCycle() { if (this.state != "Completed") { this.total++;if (this.state == "Ready") { this.waitT++; } else if (this.state == "Blocked") { this.ioTime++; } } }public void setCPUBurst(int x) { this.CCPUB = x; }public void setIOBurst(int x) { this.CCIO = x; }public int getCCPUB() { return this.CCPUB; }public int getCCIO() { return this.CCIO; }public int getrunT() { return this.runT; }public int getwaitT() { return this.waitT; }public boolean doneRunning() { CCPUB--; runT++;if (runT == cpuT || CCPUB == 0) { CCPUB = -1; return true; }return false; }public boolean doneBlocked() { CCIO--;if (CCIO == 0) { CCIO = -1; return true; }return false; }public int getTotal() { return this.total; }public String toString() { return this.arrivalT + " " + this.cpuBT + " " + this.cpuT + " " + this.ioBT + " "; }public void printStats() { System.out.println("Process " + this.NN + ":"); System.out.println("(A,B,C,IO) = (" + this.arrivalT + ", " + this.cpuBT + ", " + this.cpuT + ", " + this.ioBT + ")"); System.out.println("Finishing Time: " + this.total); System.out.println("Turnaround Time: " + (this.total - this.arrivalT)); System.out.println("I/O Time: " + this.ioTime); System.out.println("Waiting Time: " + this.waitT + "n"); } }
